const express = require('express');
const axios = require('axios');
const app = express();
const cors = require('cors');

app.use(express.json());
app.use(cors()); 

// Ustawienie nagłówków CORS
app.use((req, res, next) => {
  res.setHeader('Access-Control-Allow-Origin', '*');
  res.setHeader('Access-Control-Allow-Methods', 'GET, POST, PUT, DELETE');
  res.setHeader('Access-Control-Allow-Headers', 'Content-Type, Authorization, X-Chainlink-EA-AccessKey, X-Chainlink-EA-Secret');
  next();
});

app.post('/sendBuy', async (req, res) => {
  try {
    const data = req.body.data; // Pobieramy wartość z przesłanych danych

    const headers = {
      'Content-Type': 'application/json',
      'X-Chainlink-EA-AccessKey': 'ba3b0e9ffec34c8ab7db737992ec2d6d',
      'X-Chainlink-EA-Secret': 'mHc3Hdx4fyctCm0aWqsXYeLyRXuTuim/3JSd0uo7AdwjsCOjNX5DgPN+V5rhlFhz',
    };

    const url = `http://43.157.58.142:6687/v2/jobs/d18143cb-8825-4666-833d-65baaf6aa727/runs`;

    // Wykonujemy żądanie POST z przesłanymi danymi i nagłówkami za pomocą axios
    const response = await axios.post(url, { data }, { headers });

    res.status(response.status).json(response.data);
  } catch (error) {
    res.status(500).json({ error: error.message });
  }
});


app.listen(3001, () => {
  console.log('Server is running on port 3001');
});
